package lepartycious.daos.implementations;import lepartycious.daos.BaseDAO;import org.hibernate.Session;import org.hibernate.SessionFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Repository;@Repositorypublic class BaseDAOImpl implements BaseDAO{	@Autowired	private SessionFactory sessionFactory;	@Override	public Long save(Object object) {		Long entityId = (Long) sessionFactory.getCurrentSession().save(object);		return entityId;	}	@Override	public void delete(Object object) {		sessionFactory.getCurrentSession().delete(object);	}	@Override	public void update(Object object) {		sessionFactory.getCurrentSession().update(object);	}	@Override	public Object getObjectById(Long id, Class clazz) {		Session session = sessionFactory.getCurrentSession();		return session.get(clazz, id);	}}