package lepartycious.daos.implementations;import lepartycious.daos.BaseDAO;import org.hibernate.SessionFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Repository;@Repositorypublic class BaseDAOImpl implements BaseDAO{	@Autowired	private SessionFactory sessionFactory;	@Override	public void save(Object object) {		sessionFactory.getCurrentSession().save(object);	}	@Override	public void delete(Object object) {		sessionFactory.getCurrentSession().delete(object);	}	@Override	public void update(Object object) {		sessionFactory.getCurrentSession().update(object);	}	@Override	public void getObjectById(Long id) {	}}